// DonationApiClient.cs\nusing System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Text.Json;\nusing System.Threading.Tasks;\n\npublic class DonationApiClient\n{\n    private readonly HttpClient _httpClient;\n\n    public DonationApiClient(HttpClient httpClient)\n    {\n        _httpClient = httpClient;\n    }\n\n    public async Task<ContentResult> ConfirmDonationAsync(DonationDto donation)\n    {\n        var json = JsonSerializer.Serialize(donation);\n        var content = new StringContent(json, Encoding.UTF8, \"application/json\");\n        var response = await _httpClient.PostAsync(\"/api/v1/donation/confirm\", content);\n\n        var result = new ContentResult\n        {\n            Content = await response.Content.ReadAsStringAsync(),\n            ContentType = response.Content.Headers.ContentType?.MediaType,\n            StatusCode = (int?)response.StatusCode\n        };\n\n        return result;\n    }\n}